from typing import List

class Solution:
    def canSplitArray(self, nums: List[int], m: int) -> bool:
        n = len(nums)
        prefix_sum = [0] * (n + 1)
        for i in range(n):
            prefix_sum[i + 1] = prefix_sum[i] + nums[i]

        def can_split(mid):
            count, i = 0, 0
            for j in range(1, n + 1):
                if prefix_sum[j] - prefix_sum[i] > mid:
                    i = j - 1
                    count += 1
            return count + 1 <= n

        left, right = max(nums), prefix_sum[n]
        if right < m:
            return False

        while left < right:
            mid = (left + right) // 2
            if can_split(mid):
                right = mid
            else:
                left = mid + 1

        # Check if it's possible to split the array according to the original problem statement
        if left < m:
            return False

        # Additional checks specific to the problem constraints
        sum_subarrays = [0] * n
        for i in range(n):
            sum_subarrays[i] = nums[i]
            if i > 0:
                if sum_subarrays[i - 1] < m and sum_subarrays[i] + sum_subarrays[i - 1] < m:
                    return False
                if sum_subarrays[i - 1] < m:
                    sum_subarrays[i] += sum_subarrays[i - 1]

        return True
