object Solution {
  def canSplitArray(nums: List[Int], m: Int): Boolean = {
    // Recursive function to check if the array can be split into valid subarrays.
    def check(parts: Int, index: Int, currentSum: Int, maxParts: Int): Boolean = {
      if (index == nums.length) {
        // If we've reached the end, we should have exactly `maxParts` parts.
        return parts == maxParts && currentSum >= m
      }
      if (currentSum >= m) {
        // If current sum is enough, we can choose to split here or not.
        if (check(parts + 1, index + 1, nums(index), maxParts)) return true
      }
      // Continue adding to the current subarray.
      if (check(parts, index + 1, currentSum + nums(index), maxParts)) return true

      false
    }

    // The goal is to split into `nums.length` parts, each part is a valid subarray.
    nums.indices.exists(start => check(1, start + 1, nums(start), nums.length))
  }
}
