object Solution {
    def canSplitArray(nums: List[Int], m: Int): Boolean = {
        // Base case: if sum of all elements is less than m and length is more than 1, cannot split as required
        if (nums.sum < m && nums.length > 1) return false

        // Check if it's possible to split the array into valid subarrays
        def isValidSplit(start: Int, end: Int, splits: Int): Boolean = {
            // If no more splits are required, return true
            if (splits == 0) return true

            var sum = 0
            for (i <- start until end) {
                sum += nums(i)
                // If sum >= m, we can attempt to split here
                if (sum >= m) {
                    // Check if the rest of the array can be split into the required number of subarrays
                    if (isValidSplit(i + 1, end, splits - 1)) return true
                }
            }
            false
        }

        // Attempt to split the array into (nums.length) subarrays
        isValidSplit(0, nums.length, nums.length - 1)
    }
}
