class Solution {
    func canSplitArray(_ nums: [Int], _ m: Int) -> Bool {
        // Calculate the total sum to check if the entire array can be one segment.
        let totalSum = nums.reduce(0, +)

        // If the total sum is less than m, it's not possible to split as required.
        if totalSum < m {
            return false
        }

        // If nums contains exactly one element, return true if it's >= m or false otherwise.
        if nums.count == 1 {
            return nums[0] >= m
        }

        // Dynamic programming cache to remember subproblems' solutions.
        var memo = [String: Bool]()

        // Helper function to check feasibility of splitting from index `start` with `count` splits remaining.
        func canSplit(from start: Int, remainingSplits: Int, currentSum: Int) -> Bool {
            // Base case: if start reaches the end of nums, check if we've made all splits.
            if start == nums.count {
                return remainingSplits == 0
            }

            // If only one split remains, we can directly check the sum of the remaining elements.
            if remainingSplits == 1 {
                let sumRemaining = nums[start...].reduce(0, +)
                return sumRemaining >= m
            }

            let key = "\(start)-\(remainingSplits)-\(currentSum)"
            if let cached = memo[key] {
                return cached
            }

            var result = false
            var sum = currentSum

            for i in start..<nums.count {
                sum += nums[i]
                // Once the sum reaches m, we try to split here.
                if sum >= m {
                    if canSplit(from: i + 1, remainingSplits: remainingSplits - 1, currentSum: 0) {
                        result = true
                        break
                    }
                }
            }

            memo[key] = result
            return result
        }

        return canSplit(from: 0, remainingSplits: nums.count, currentSum: 0)
    }
}
