function canSplitArray(nums: number[], m: number): boolean {
    // Helper function to check if it's possible to split the array into n subarrays
    // with each subarray sum >= m (except for single-element subarrays)
    function canSplit(start: number, subarraysCount: number, currentSum: number): boolean {
        // Base case: If we've reached the end of the array
        if (start == nums.length) {
            return subarraysCount == nums.length && currentSum >= m;
        }

        // If adding the current element to the current subarray doesn't exceed the sum,
        // continue adding to the current subarray.
        if (currentSum + nums[start] >= m) {
            if (canSplit(start + 1, subarraysCount + 1, 0)) {
                return true;
            }
        }

        // Try to start a new subarray from the next element, if it's not the last element
        if (start + 1 < nums.length || currentSum + nums[start] >= m) {
            if (canSplit(start + 1, subarraysCount, currentSum + nums[start])) {
                return true;
            }
        }

        // If none of the above work, return false
        return false;
    }

    // Initial call to the helper function, starting from the first element
    return canSplit(0, 0, 0);
}
