public class Solution {
    public bool CheckStrings(string s1, string s2) {
        // Initialize counters for characters at even and odd positions for both strings
        int[] evenCounts1 = new int[26], evenCounts2 = new int[26];
        int[] oddCounts1 = new int[26], oddCounts2 = new int[26];

        // Populate the counters
        for (int i = 0; i < s1.Length; i++) {
            if (i % 2 == 0) { // Even index
                evenCounts1[s1[i] - 'a']++;
                evenCounts2[s2[i] - 'a']++;
            } else { // Odd index
                oddCounts1[s1[i] - 'a']++;
                oddCounts2[s2[i] - 'a']++;
            }
        }

        // Compare the counters for even and odd indices separately
        for (int i = 0; i < 26; i++) {
            if (evenCounts1[i] != evenCounts2[i] || oddCounts1[i] != oddCounts2[i]) {
                return false;
            }
        }

        // If all counts match, the strings can be made equal
        return true;
    }
}
