class Solution {
public:
    bool checkStrings(string s1, string s2) {
        // Arrays to store frequency of characters at even and odd indices
        vector<int> s1_even(26, 0), s1_odd(26, 0), s2_even(26, 0), s2_odd(26, 0);

        // Count the frequency of each character at even and odd indices for both strings
        for (int i = 0; i < s1.size(); ++i) {
            if (i % 2 == 0) { // Even index
                s1_even[s1[i] - 'a']++;
                s2_even[s2[i] - 'a']++;
            } else { // Odd index
                s1_odd[s1[i] - 'a']++;
                s2_odd[s2[i] - 'a']++;
            }
        }

        // Check if the frequency of characters at even and odd indices matches between s1 and s2
        return s1_even == s2_even && s1_odd == s2_odd;
    }
};
