#include <stdbool.h>  // For bool type
#include <string.h>   // For strlen function

bool checkStrings(char * s1, char * s2) {
    // Assuming the input strings consist only of lowercase English letters
    int count1[26][2] = {0}; // Count of each character in s1; [character][0 for even, 1 for odd]
    int count2[26][2] = {0}; // Count of each character in s2; [character][0 for even, 1 for odd]

    // Calculate the length of the strings (both have the same length as per the problem statement)
    int n = strlen(s1);

    // Count occurrences of each character at even and odd indices separately
    for (int i = 0; i < n; ++i) {
        count1[s1[i] - 'a'][i % 2]++;
        count2[s2[i] - 'a'][i % 2]++;
    }

    // Check if the counts match for all characters at both even and odd indices
    for (int i = 0; i < 26; ++i) {
        if (count1[i][0] != count2[i][0] || count1[i][1] != count2[i][1]) {
            // If counts don't match for any character, return false
            return false;
        }
    }

    // If all counts match, return true
    return true;
}
