impl Solution {
    pub fn check_strings(s1: String, s2: String) -> bool {
        // Convert both strings to character vectors to facilitate operations
        let mut s1_chars: Vec<char> = s1.chars().collect();
        let mut s2_chars: Vec<char> = s2.chars().collect();

        // Separate odd and even indexed characters for s1 and s2
        let (mut s1_even, mut s1_odd) = (Vec::new(), Vec::new());
        let (mut s2_even, mut s2_odd) = (Vec::new(), Vec::new());

        // Distribute characters based on their index
        for i in 0..s1_chars.len() {
            if i % 2 == 0 { // Even index
                s1_even.push(s1_chars[i]);
                s2_even.push(s2_chars[i]);
            } else { // Odd index
                s1_odd.push(s1_chars[i]);
                s2_odd.push(s2_chars[i]);
            }
        }

        // Sort the characters in each vector to compare them
        s1_even.sort_unstable();
        s1_odd.sort_unstable();
        s2_even.sort_unstable();
        s2_odd.sort_unstable();

        // Return true if both odd and even characters match between s1 and s2
        s1_even == s2_even && s1_odd == s2_odd
    }
}
