object Solution {
    def checkStrings(s1: String, s2: String): Boolean = {
        // Function to count characters at even and odd positions
        def countChars(s: String): (Map[Char, Int], Map[Char, Int]) = {
            s.zipWithIndex.foldLeft((Map[Char, Int](), Map[Char, Int]())) {
                case ((even, odd), (char, index)) =>
                    if (index % 2 == 0) (even + (char -> (even.getOrElse(char, 0) + 1)), odd)
                    else (even, odd + (char -> (odd.getOrElse(char, 0) + 1)))
            }
        }

        val (even1, odd1) = countChars(s1)
        val (even2, odd2) = countChars(s2)

        // Check if the counts of even and odd characters match between s1 and s2
        even1 == even2 && odd1 == odd2
    }
}
