#include <vector>
#include <algorithm> // For std::min
#include <numeric>   // For std::accumulate

class Solution {
public:
    long long minCost(vector<int>& nums, int x) {
        int n = nums.size();
        // Initial cost is set to the sum of all chocolates as we have to buy each type at least once
        long long totalCost = std::accumulate(nums.begin(), nums.end(), 0LL);
        long long minCost = totalCost; // This will store the final minimum cost

        for (int start = 0; start < n; ++start) {
            long long cost = 0; // Cost including operations
            long long opCost = 0; // Cost of operations alone

            for (int i = 0; i < n; ++i) {
                int currentIdx = (start + i) % n;
                opCost = (long long)i * x;
                cost += opCost + nums[currentIdx];

                // After buying each type of chocolate at least once, we check if the current
                // cost is the minimum. However, this needs to be adjusted as we add the totalCost
                // initially assuming no operations. We subtract the cost of the current chocolate
                // since it's included in the totalCost and add the operation cost.
                if (i == n - 1) { // Ensure all types are considered
                    minCost = std::min(minCost, totalCost + (i * x) - nums[currentIdx] + opCost);
                }
            }
        }

        return minCost;
    }
};
