public class Solution {
    public int LongestString(int x, int y, int z) {
        // The strategy is to place "AB" between "AA" and "BB" to maximize length without causing "AAA" or "BBB".
        // The goal is to find the maximum length possible under these conditions.

        // Start with using all "AB" strings, as they can always be included.
        int totalLength = z * 2;

        // Determine how to use "AA" and "BB" optimally.
        // First, ensure that "AA" and "BB" are used in such a way to not exceed the "AAA" or "BBB" limit.
        int minABNeeded = Math.Abs(x - y); // Minimum "AB" needed to separate excess "AA" or "BB"

        if (z >= minABNeeded) {
            // If we have enough "AB" to separate excess "AA" or "BB", we can use all "AA" and "BB".
            totalLength += (x + y) * 2;
            // After separating the excess, each additional "AB" can be paired with an "AA" or "BB" not to waste any "AB".
            int leftoverAB = z - minABNeeded;
            // Add leftover "AB" pairs to total length, but only up to the number of "AA" or "BB".
            totalLength += Math.Min(leftoverAB, x + y) * 2;
        } else {
            // If not enough "AB" to separate all excess "AA" or "BB", use as many as we can to maximize the length.
            // This involves alternating "AA" and "BB" with "AB" until "AB" runs out.
            totalLength += (z * 2); // Pair each "AB" with an "AA" or "BB".
            if (x > y) {
                // Use one more "AA" if possible.
                totalLength += Math.Min(2 * (y + 1), x * 2);
            } else {
                // Use one more "BB" if possible.
                totalLength += Math.Min(2 * (x + 1), y * 2);
            }
        }

        return totalLength;
    }
}
