public class Solution {
    public int LongestString(int x, int y, int z) {
        // The core idea is to use "AA" and "BB" as much as possible while ensuring no "AAA" or "BBB".
        // "AB" is used either as a separator or to maximize the length by alternation.

        int totalLength = 0; // Initialize the total length of the concatenated string.

        // If we have more "AB" strings or an equal amount to the difference between "AA" and "BB",
        // we can just alternate them, ensuring no "AAA" or "BBB" sequence occurs.
        if (z >= Math.Abs(x - y) - 1) {
            // We use all "AA" and "BB" strings plus all "AB" strings.
            totalLength += x * 2 + y * 2 + z * 2;
        } else {
            // If "AB" strings are not enough to alternate all "AA" and "BB" perfectly,
            // we focus on maximizing the use of "AB" to prevent "AAA" or "BBB".
            if (x > y) {
                // If we have more "AA", we use "AB" to separate them and use all "BB".
                totalLength += y * 2 + (y + 1) * 2 + z * 2;
            } else {
                // If we have more "BB", we use "AB" to separate them and use all "AA".
                totalLength += x * 2 + (x + 1) * 2 + z * 2;
            }
        }

        // The calculation above might overestimate the length when the number of "AB" strings is not sufficient
        // to alternate between all "AA" and "BB" without resulting in "AAA" or "BBB".
        // Thus, we need to adjust the calculation to ensure it reflects the maximum length accurately.

        // Correcting the overestimation by ensuring we do not exceed the bounds set by the problem constraints.
        int maxPairs = Math.Min(x, y) + z; // Maximum pairs of "AA"-"BB" that can be formed with or without "AB".
        if (x != y) {
            // Adjusting for the case where there's a difference between "AA" and "BB" counts.
            totalLength = Math.Min(totalLength, (2 * maxPairs + 2 * Math.Min(Math.Max(x, y), maxPairs + 1)));
        } else {
            // When "AA" and "BB" counts are equal, we can directly use all with "AB" to form the longest string.
            totalLength = Math.Min(totalLength, 2 * (x + y + z));
        }

        return totalLength;
    }
}
