class Solution {
public:
    int longestString(int x, int y, int z) {
        // Firstly, handle the edge case where the "AB" strings can perfectly intersperse "AA" and "BB"
        // such that they can form a seamless alternating pattern without creating "AAA" or "BBB".
        // The maximum length here is twice the sum of all inputs, as each string contributes 2 characters.
        if (z >= abs(x - y)) {
            int minPairs = std::min(x, y); // Minimum of "AA" or "BB"
            int maxPairs = std::max(x, y); // Maximum of "AA" or "BB"
            // Every "AA" and "BB" can be used, interspersed by "AB" as needed.
            // Additional "AB" can be placed at the beginning or end, or between any "AA" and "BB".
            return 2 * (minPairs + maxPairs + z);
        }

        // If "AB" is not enough to intersperse all "AA" and "BB", we use them to balance out the counts of "AA" and "BB".
        // After balancing "AA" and "BB" as much as possible, we alternate "AA" and "BB" to avoid triplets.
        int balancedLength = 2 * (x + y + z); // Each string contributes 2 characters to the length.
        // If there's a difference greater than "z", it means we can't intersperse all "AA" or "BB" without forming triplets.
        // Thus, we need to adjust the length to account for the maximum allowable sequence.
        if (x > y + z) {
            // Too many "AA", adjust the length considering "BB" and "AB" can't fully intersperse "AA".
            balancedLength -= 2 * (x - (y + z + 1));
        } else if (y > x + z) {
            // Too many "BB", similar adjustment.
            balancedLength -= 2 * (y - (x + z + 1));
        }

        return balancedLength;
    }
};
