        // Use "AB" to separate or extend the sequence
        int useAB = (x > 2) ? 2 : x; // We can only place two more "AA" separated by "AB"
        x -= useAB; // Subtract the "AA"s we can still use
        maxLength += useAB * 2; // Add the "AA" lengths
        // Add "AB" length
