class Solution {
    public int longestString(int x, int y, int z) {
        // First, ensure x is not less than y to simplify the logic, swapping if necessary
        if (x < y) {
            int temp = x;
            x = y;
            y = temp;
        }

        // The approach is to add "AA" and "BB" as much as possible, then use "AB" to increase the length.
        int result = 0;

        // Adding "AA" and "BB" directly, ensuring not to exceed the "AAA" or "BBB" limit
        if (x > 2) {
            if (y > 0) {
                result += 2 * Math.min(2, y) + 2; // Add "BB" for each "AA" if possible
                x -= Math.min(2, y) + 1; // Used one more "AA" than "BB"
                y -= Math.min(2, y);
            } else {
                result += 4; // Only "AA" can be used twice
                x -= 2;
            }
        } else {
            result += 2 * (x + y); // Use all "AA" and "BB" directly if both are <= 2
            x = y = 0;
        }

        // Use "AB" to alternate between remaining "AA" and "BB"
        int minAB = Math.min(z, x + y);
        result += 2 * minAB;
        z -= minAB;

        // If "AB" still remains, it can only be placed at the beginning or end
        if (z > 0) {
            result += 2; // Only one "AB" can be effectively used at the start or end, not both
        }

        return result;
    }
}
