class Solution {
    fun longestString(x: Int, y: Int, z: Int): Int {
        // Initialize counters for "AA", "BB", and "AB" strings
        var aa = x
        var bb = y
        var ab = z

        // Start with adding "AA" or "BB" whichever is more
        var result = 0
        if (aa > bb) {
            // Add one "AA" if possible
            if (aa > 0) {
                result += 2
                aa--
            }
        } else {
            // Add one "BB" if possible
            if (bb > 0) {
                result += 2
                bb--
            }
        }

        // Now, add "AB", alternating with "AA" and "BB"
        while (ab > 0 || aa > 0 || bb > 0) {
            // If we have "AB" to use
            if (ab > 0) {
                // Alternate adding "AB" with whichever is available
                if (aa > bb && aa > 0 || bb == 0) {
                    result += 2 // Add "AA"
                    aa--
                } else if (bb >= aa && bb > 0) {
                    result += 2 // Add "BB"
                    bb--
                }
                result += 2 // Add "AB"
                ab--
            } else {
                // If no "AB" left, carefully add "AA" or "BB"
                if (aa > 0) {
                    result += 2 * minOf(aa, 2) // Can add at most two "AA" to avoid "AAA"
                    break // End loop as we cannot add more without "AB"
                }
                if (bb > 0) {
                    result += 2 * minOf(bb, 2) // Can add at most two "BB" to avoid "BBB"
                    break // End loop as we cannot add more without "AB"
                }
            }
        }

        return result
    }
}
