class Solution {
    fun longestString(x: Int, y: Int, z: Int): Int {
        // Strategy:
        // 1. Use "AB" to separate "AA" and "BB" as much as possible.
        // 2. Alternate "AA" and "BB" to prevent "AAA" or "BBB".
        // 3. Maximize the use of "AA" and "BB" given the constraints.

        // Calculate the core length without considering "AB" strings.
        // This is the maximum possible length using just "AA" and "BB" strings.
        val maxCoreLength = Math.min(x, y) * 2 * 2 // *2 for pairs, *2 for "AA"/"BB" length

        // Determine the extra "AA" or "BB" beyond the balanced core length.
        val extra = Math.abs(x - y)

        // Calculate length considering "AB" strings.
        // Each "AB" can potentially add 2 to the length, but must also account for extra "AA" or "BB".
        // The key is to use "AB" to effectively separate excess "AA" or "BB".
        val maxLengthWithAB = if (z >= extra) {
            // If we have more "AB" than extra "AA"/"BB", we can fully separate them and use additional "AB".
            maxCoreLength + extra * 2 + Math.min(z - extra, Math.min(x, y) + 1) * 2
        } else {
            // If "AB" is not enough to separate all extras, use all "AB" to intersperse the extras.
            maxCoreLength + z * 2
        }

        return maxLengthWithAB
    }
}
