class Solution {

    /**
     * @param Integer $x
     * @param Integer $y
     * @param Integer $z
     * @return Integer
     */
    function longestString($x, $y, $z) {
        // First, sort the counts of "AA" and "BB" strings to always use the one we have more of first.
        if ($x < $y) {
            list($x, $y) = [$y, $x]; // Swap $x and $y if $x is less than $y
        }

        $maxLen = 0;
        // Use "AB" as much as possible as a separator between "AA" and "BB"
        // "AB" can be placed between "AA" and "BB" or at the end/start if one type is exhausted
        if ($z >= abs($x - $y)) {
            // If we have enough "AB" to balance out the difference between "AA" and "BB"
            $maxLen += 2 * $z + $x + $y;
        } else {
            // Not enough "AB" to balance, some "AA" or "BB" will be left without pair
            $maxLen += 2 * $z;
            if ($x > $y) {
                // We can only add two more "AA" than "BB" without creating "AAA"
                $maxLen += 2 * min($x - $z, $y + 1);
            } else {
                // We can only add two more "BB" than "AA" without creating "BBB"
                $maxLen += 2 * min($y - $z, $x + 1);
            }
        }
        return $maxLen;
    }
}
