class Solution {

    /**
     * @param Integer $x
     * @param Integer $y
     * @param Integer $z
     * @return Integer
     */
    function longestString($x, $y, $z) {
        // The maximum length of "AA" or "BB" is limited to the sum of x and y, plus twice the z (as "AB" can act as a buffer).
        // However, to avoid "AAA" or "BBB", we can only add an "AA" or "BB" after using an "AB" if available.

        // Start with calculating the base without "AB"
        $base = min($x, 2) + min($y, 2); // At most 2 "AA" and 2 "BB"

        // If we have more "AA" and "BB" than "AB" can separate, we need to adjust the calculation
        if ($z > 0) {
            // Add all "AB" we can use
            $base += $z * 2;
            // Add extra "AA" or "BB" if we can insert them between "AB"s
            $extraAA = max(0, $x - 2); // Subtract the two "AA" we've already potentially added
            $extraBB = max(0, $y - 2); // Subtract the two "BB" we've already potentially added
            // We can insert "AA" or "BB" between "AB"s if there are enough "AB"s to do so
            $insertable = min($extraAA + $extraBB, $z - 1); // We can insert between "AB"s, not at the ends without "AAA" or "BBB"
            $base += $insertable;
        }

        // The remaining "AA" or "BB" that couldn't be optimally placed due to lack of "AB"
        $remaining = max(0, $x - 2) + max(0, $y - 2) - ($z - 1);
        $remaining = max(0, $remaining); // Ensure it's not negative

        return min($x * 2 + $y * 2 + $z * 2, $base + $remaining); // The total length cannot exceed the sum of all strings if placed without constraints
    }
}
