impl Solution {
    pub fn longest_string(x: i32, y: i32, z: i32) -> i32 {
        // Calculate the initial possible sequences of "AA" and "BB"
        let aa_bb_max = 2 * (x + y); // Maximum "AA" and "BB" that could potentially be used without "AB"
        let mut total = 0;

        // If there are more "AB" strings, they can be used to separate "AA" and "BB" effectively
        // Calculate the maximum length considering "AB" strings
        if z >= 1 {
            // Use "AB" as much as possible to separate "AA" and "BB"
            let separator_usage = z - 1; // "AB" used as separators
            let aa_bb_use = (x.min(y) * 2) + separator_usage.min(x.min(y) * 2);
            total += aa_bb_use + (z * 2);

            // If one of "AA" or "BB" is left in excess
            let extra = (x.max(y) - x.min(y)).min(z + 1);
            total += extra * 2;
        } else {
            // If no "AB" to separate, just alternate "AA" and "BB" directly
            total = 2 * (x.min(y) * 2);
        }

        // Adjust for the case where we can add one more "AA" or "BB" if it's left without creating "AAA" or "BBB"
        if x != y && z > 0 {
            total += 2;
        }

        // Ensuring the calculation does not exceed the maximum possible sequences
        total.min(aa_bb_max + (z * 2))
    }
}
