class Solution {
    func longestString(_ x: Int, _ y: Int, _ z: Int) -> Int {
        // The strategy is to place "AB" in between "AA" and "BB" as much as possible.
        // When "AB" is not available, we need to consider the maximum usable "AA" and "BB".

        // The difference between "AA" and "BB" strings determines the arrangement.
        let diff = abs(x - y)
        // The total "AB" strings can act as a buffer between "AA" and "BB".
        let totalAB = min(x + y, z) // Use "AB" to separate or as itself

        // If we have more "AB" strings than needed to separate, they contribute directly to length.
        // Otherwise, their count helps in alternating "AA" and "BB".
        if z >= diff {
            // When "AB" is enough to separate all "AA" and "BB", or there are more "AB"s
            // Each "AA" and "BB" can be used fully, with "AB" acting as separators or additional length.
            let baseLength = (x + y) * 2 + min(z, x + y)
            return baseLength
        } else {
            // When "AB" is not enough to perfectly alternate, we use as many as possible
            // and adjust for the imbalance between "AA" and "BB".
            let baseLength = (x + y) * 2 + z
            // Add the difference if "AB" can cover it, otherwise add what's possible.
            return baseLength + min(diff, z) * 2
        }
    }
}
