class Solution {
    func longestString(_ x: Int, _ y: Int, _ z: Int) -> Int {
        // Determine the maximum number of "AA" or "BB" that can be used
        // without forming "AAA" or "BBB", then use "AB" to separate them or extend length.

        let maxAAandBB = min(x, 2 + z) + min(y, 2 + z) // Use up to x "AA" and y "BB", but avoid "AAA" and "BBB"

        // If one of x or y is significantly larger than the other, the maximum length
        // can be reached by interleaving "AB" with the larger count until "AB" or the smaller count runs out.
        // After which, only two of the larger count can be added without violating the rules.
        // The "AB" strings can effectively be inserted in between "AA" and "BB" to avoid "AAA" or "BBB"
        // and also directly contribute to the total length if not needed for separation.

        if z >= abs(x - y) {
            // If "AB" is enough to bridge the gap between "AA" and "BB" counts
            return 2 * (x + y + z)
        } else {
            // When "AB" isn't enough to completely bridge the gap, we use them to their full extent
            // and then add the remaining "AA" or "BB", whichever is greater, but can only add up to 2 more
            // to avoid "AAA" or "BBB".
            let totalUsedAB = z
            let remainingAAorBB = abs(x - y) - totalUsedAB
            return 2 * (x + y + totalUsedAB) + min(2, remainingAAorBB) * 2
        }
    }
}
