function longestString(x: number, y: number, z: number): number {
    // If there are more "AA" than "BB", or vice versa, the limiting factor will be the lesser of the two
    // plus the ability to insert "AB" to separate excess "AA" or "BB".
    // When "AB" is used between every "AA" and "BB", it can prevent sequences of three identical letters.

    // The total possible length can be calculated by first ensuring "AA" and "BB" are alternated
    // and then adding "AB" strings to maximize length without breaking the rules.

    let totalLength = 0; // Initialize total possible length of the new string.

    // Use "AB" to separate "AA" and "BB" if one is more than the other
    if (Math.abs(x - y) <= 1) {
        // If x and y are almost equal, we can directly use all "AB" strings to maximize length
        totalLength = 2 * (x + y + z);
    } else {
        // If there's a significant difference between x and y, "AB" can only help up to a point
        if (z > 0) {
            // One "AB" can be used to balance out the difference between "AA" and "BB" once
            totalLength = 2 * (1 + Math.min(x, y) + z + Math.min(x, y));
            // After using one "AB" to separate, we need to see if we have an excess of "AA" or "BB"
            // and adjust the length accordingly
            if (x > y) {
                totalLength += 2 * Math.min(x - y - 1, z);
            } else {
                totalLength += 2 * Math.min(y - x - 1, z);
            }
        } else {
            // Without "AB", we can only alternate "AA" and "BB"
            totalLength = 2 * (1 + Math.min(x, y));
        }
    }

    return totalLength;
}

// Example tests
console.log(longestString(2, 5, 1)); // Output should be 12
