function longestString(x: number, y: number, z: number): number {
    // Find the core string length without considering "AB" initially
    let coreStringLength = 0;

    if (x === y) {
        // When x and y are equal, use all "AA" and "BB", plus "AB" in between as needed
        coreStringLength = 2 * (x + y);
    } else if (x > y) {
        // When there are more "AA"s, ensure not to form "AAA"
        coreStringLength = 2 * (2 * y + Math.min(2, x - y));
    } else {
        // When there are more "BB"s, ensure not to form "BBB"
        coreStringLength = 2 * (2 * x + Math.min(2, y - x));
    }

    // "AB" can be used to separate or extend, but its use depends on the imbalance
    let imbalance = Math.abs(x - y);
    let maxABUsage = Math.min(z, imbalance) + (z > imbalance ? 1 : 0);

    // Calculate total length considering "AB" usage
    let totalLength = coreStringLength + 2 * maxABUsage;

    // Adjust if "AB" strings are left to be used as extenders
    if (z > imbalance) {
        let remainingAB = z - imbalance - maxABUsage;
        totalLength += 2 * remainingAB;
    }

    return totalLength;
}

// Example tests
