class Solution {
    public long continuousSubarrays(int[] nums) {
        long count = 0;
        int left = 0;
        int max = nums[0], min = nums[0];

        for (int right = 0; right < nums.length; right++) {
            // Update the max and min within the current window
            max = Math.max(max, nums[right]);
            min = Math.min(min, nums[right]);

            // While the difference between max and min in the window is more than 2,
            // move the left pointer to the right and update max and min accordingly.
            while (max - min > 2) {
                if (nums[left] == max) {
                    max = getMax(nums, left + 1, right);
                }
                if (nums[left] == min) {
                    min = getMin(nums, left + 1, right);
                }
                left++;
            }

            // Add the number of continuous subarrays ending at `right`
            count += right - left + 1;
        }

        return count;
    }

    // Helper method to find the max within a range of the array
    private int getMax(int[] nums, int start, int end) {
        int max = nums[start];
        for (int i = start + 1; i <= end; i++) {
            max = Math.max(max, nums[i]);
        }
        return max;
    }

    // Helper method to find the min within a range of the array
    private int getMin(int[] nums, int start, int end) {
        int min = nums[start];
        for (int i = start + 1; i <= end; i++) {
            min = Math.min(min, nums[i]);
        }
        return min;
    }
}
