function continuousSubarrays(nums: number[]): number {
    let total = 0;
    let start = 0, end = 0;
    let min = nums[0], max = nums[0];

    while (end < nums.length) {
        // Update min and max for the current window
        min = Math.min(min, nums[end]);
        max = Math.max(max, nums[end]);

        // While the current window is valid, move `end` forward and update `total`
        if (max - min <= 2) {
            // The number of subarrays ending at `end` and starting from any index between `start` and `end` inclusive
            total += (end - start + 1);
            end++;
        } else {
            // If the window is invalid, move `start` forward and adjust min and max accordingly
            start++;
            // Reset min and max for the new window
            min = nums[start];
            max = nums[start];
            // Adjust for the case where `start` surpasses `end`
            if (start > end) {
                end = start;
                min = nums[start];
                max = nums[start];
            } else {
                // Recalculate min and max for the current window since it has changed
                for (let i = start; i <= end; i++) {
                    min = Math.min(min, nums[i]);
                    max = Math.max(max, nums[i]);
                }
            }
        }
    }

    return total;
}
