#include <string>
using namespace std;

class Solution {
public:
    int beautifulSubstrings(string s, int k) {
        int count = 0; // To keep track of the number of beautiful substrings

        // Helper lambda to check if a character is a vowel
        auto isVowel = [](char ch) {
            return ch == 'a' || ch == 'e' || ch == 'i' || ch == 'o' || ch == 'u';
        };

        // Iterate through all possible substrings
        for (size_t i = 0; i < s.length(); ++i) {
            int vowels = 0, consonants = 0;
            for (size_t j = i; j < s.length(); ++j) {
                // Increment the appropriate counter based on whether the current character is a vowel
                isVowel(s[j]) ? ++vowels : ++consonants;

                // Check if the current substring is beautiful
                if (vowels == consonants && (vowels * consonants) % k == 0) {
                    ++count;
                }
            }
        }

        return count;
    }
};
