long long countInterestingSubarrays(int* nums, int numsSize, int modulo, int k){
    long long result = 0;
    int* prefixSum = (int*)malloc(sizeof(int) * (numsSize + 1));
    int* count = (int*)calloc(modulo, sizeof(int));
    prefixSum[0] = 0;

    for (int i = 1; i <= numsSize; i++) {
        if (nums[i - 1] % modulo == k) {
            prefixSum[i] = (prefixSum[i - 1] + 1) % modulo;
        } else {
            prefixSum[i] = prefixSum[i - 1];
        }
    }

    count[0] = 1; // Base case: empty subarray
    for (int i = 1; i <= numsSize; i++) {
        result += count[prefixSum[i]];
        count[prefixSum[i]]++;
    }

    free(prefixSum);
    free(count);
    return result;
}
