class Solution {
    public long countInterestingSubarrays(List<Integer> nums, int modulo, int k) {
        long count = 0;
        Map<Integer, Integer> prefixSum = new HashMap<>();
        prefixSum.put(0, 1); // Initialize with a sum of 0 having 1 occurrence
        int currentSum = 0;

        for (int num : nums) {
            // If the current number satisfies the condition, increase the sum
            if (num % modulo == k) currentSum++;
            // Adjust currentSum to be within the range [0, modulo - 1]
            int adjustedSum = (currentSum - k) % modulo;
            if (adjustedSum < 0) adjustedSum += modulo;

            // Add the occurrences of the adjustedSum to the count
            count += prefixSum.getOrDefault(adjustedSum, 0);

            // Increment the occurrence of the currentSum in the map
            prefixSum.put(currentSum % modulo, prefixSum.getOrDefault(currentSum % modulo, 0) + 1);
        }

        return count;
    }
}
