class Solution {
    fun countInterestingSubarrays(nums: List<Int>, modulo: Int, k: Int): Long {
        val prefixCounts = LongArray(nums.size + 1)
        val countMap = mutableMapOf<Long, Long>()
        var count = 0L
        var interestingCount = 0L

        countMap[0] = 1L

        for (i in nums.indices) {
            if (nums[i] % modulo == k) count++
            prefixCounts[i + 1] = count

            val target = (count - k + modulo) % modulo
            interestingCount += countMap.getOrDefault(target, 0L)

            countMap[prefixCounts[i + 1] % modulo] = countMap.getOrDefault(prefixCounts[i + 1] % modulo, 0L) + 1
        }

        return interestingCount
    }
}
