impl Solution {
    pub fn count_interesting_subarrays(nums: Vec<i32>, modulo: i32, k: i32) -> i64 {
        let mut count = 0i64;
        let mut freq = std::collections::HashMap::new();
        freq.insert(0, 1i64);
        let mut sum = 0;
        let mut interesting_count = 0;

        for num in nums {
            sum = (sum + if num % modulo == k { 1 } else { 0 }) % modulo;
            interesting_count += *freq.get(&((sum + modulo - k) % modulo)).unwrap_or(&0);
            *freq.entry(sum).or_insert(0) += 1;
        }

        interesting_count
    }
}
