function countInterestingSubarrays(nums: number[], modulo: number, k: number): number {
    let count = 0;
    const prefixSum = new Map<number, number>();
    let currentSum = 0;
    prefixSum.set(0, 1); // Initialize with sum 0 having 1 occurrence

    for (let num of nums) {
        if (num % modulo == k) {
            currentSum = (currentSum + 1) % modulo;
        }
        const targetSum = (currentSum - k + modulo) % modulo;
        count += (prefixSum.get(targetSum) || 0);
        prefixSum.set(currentSum, (prefixSum.get(currentSum) || 0) + 1);
    }

    return count;
};
