class Solution {
public:
    int countPairs(vector<vector<int>>& coordinates, int k) {
        int count = 0;
        unordered_map<int, vector<int>> xs, ys; // Maps to keep track of x and y coordinates separately

        // Separately store indices of points with the same x or y coordinate
        for (int i = 0; i < coordinates.size(); ++i) {
            int x = coordinates[i][0], y = coordinates[i][1];
            xs[x].push_back(i);
            ys[y].push_back(i);
        }

        unordered_set<string> uniquePairs; // To avoid counting duplicates

        for (int i = 0; i < coordinates.size(); ++i) {
            int x = coordinates[i][0], y = coordinates[i][1];

            // Look for points with the same x value
            for (int j : xs[x ^ k]) {
                if (y == coordinates[j][1]) {
                    int small = min(i, j), large = max(i, j);
                    uniquePairs.insert(to_string(small) + "," + to_string(large));
                }
            }

            // Look for points with the same y value
            for (int j : ys[y ^ k]) {
                if (x == coordinates[j][0]) {
                    int small = min(i, j), large = max(i, j);
                    uniquePairs.insert(to_string(small) + "," + to_string(large));
                }
            }
        }

        count = uniquePairs.size();
        return count;
    }
};
