class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> maxIndex;
        int maxNum = *max_element(nums.begin(), nums.end());
        for (int i = 0; i < n; ++i) {
            if (nums[i] == maxNum) maxIndex.push_back(i);
        }
        if (maxIndex.size() < k) return 0;
        long long ans = 0;
        for (int i = 0; i + k - 1 < maxIndex.size(); ++i) {
            long long left = i == 0 ? maxIndex[i] + 1 : maxIndex[i] - maxIndex[i - 1];
            long long right = i + k - 1 == maxIndex.size() - 1 ? n - maxIndex[i + k - 1] : maxIndex[i + k] - maxIndex[i + k - 1];
            ans += left * right;
        }
        return ans;
    }
};
