class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();
        vector<int> freq(1000001, 0); // Frequency array for numbers
        int left = 0, right = 0, count = 0; // Two pointers and count of maximum element

        int maxNum = *max_element(nums.begin(), nums.end());
        while (right < n) {
            // Increase frequency of the current element
            freq[nums[right]]++;
            // Update count if it's the max element
            if (nums[right] == maxNum) {
                count++;
            }
            // If count of max element reaches k, start contracting from the left
            while (count >= k) {
                ans += (n - right); // Add subarrays ending at right
                if (nums[left] == maxNum) {
                    count--;
                }
                freq[nums[left]]--; // Decrease frequency of the leftmost element
                left++;
            }
            right++;
        }
        return ans;
    }
};
