class Solution {
    public long countSubarrays(int[] nums, int k) {
        long count = 0;
        int n = nums.length;
        int maxNum = 0;
        for (int num : nums) {
            maxNum = Math.max(maxNum, num);
        }
        int[] freq = new int[n];
        int left = 0, right = 0, maxFreq = 0;
        while (right < n) {
            freq[nums[right]]++;
            if (nums[right] == maxNum) {
                maxFreq++;
            }
            while (maxFreq >= k) {
                count += n - right;
                freq[nums[left]]--;
                if (nums[left] == maxNum) {
                    maxFreq--;
                }
                left++;
            }
            right++;
        }
        return count;
    }
}
