/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var countSubarrays = function(nums, k) {
    let n = nums.length;
    let maxNum = Math.max(...nums);
    let counts = new Array(n).fill(0);
    let last = -1, ans = 0;

    for (let i = 0; i < n; ++i) {
        if (nums[i] == maxNum) {
            if (i - last - 1 >= k) {
                for (let j = last + 1; j <= i - k; ++j) {
                    counts[j] = i - j;
                }
            }
            last = i;
        }
    }

    if (n - last - 1 >= k) {
        for (let i = last + 1; i <= n - k; ++i) {
            counts[i] = n - i;
        }
    }

    for (let i = 0; i <= n - k; ++i) {
        if (counts[i] > 0) {
            for (let j = i + k - 1; j < i + counts[i]; ++j) {
                ans += counts[i] - (j - i);
            }
        }
    }

    return ans;
};
