class Solution {
    fun countSubarrays(nums: IntArray, k: Int): Long {
        val max = nums.maxOrNull()!!
        val positions = ArrayList<Int>()
        for (i in nums.indices) {
            if (nums[i] == max) positions.add(i)
        }
        if (positions.size < k) return 0
        var count = 0L
        for (i in 0 until k - 1) {
            count += (positions[i] - (if (i == 0) -1 else positions[i - 1])) * (positions[k - 1] - positions[i]).toLong()
        }
        for (i in k until positions.size) {
            val left = positions[i - k + 1] - positions[i - k]
            val right = positions[i] - positions[i - 1]
            count += left * right.toLong()
        }
        count += (positions[positions.size - k] - (if (positions.size - k == 0) -1 else positions[positions.size - k - 1])) * (nums.size - positions[positions.size - 1]).toLong()
        return count
    }
}
