class Solution {
    fun countSubarrays(nums: IntArray, k: Int): Long {
        var count = 0L
        val maxNum = nums.maxOrNull() ?: return 0
        val occurrences = mutableListOf<Int>()

        nums.forEachIndexed { index, num ->
            if (num == maxNum) occurrences.add(index)
        }

        if (occurrences.size < k) return 0

        for (i in occurrences.indices) {
            val start = if (i == 0) 0 else occurrences[i - 1] + 1
            val end = if (i + k - 1 < occurrences.size) occurrences[i + k - 1] else nums.lastIndex

            for (j in start..end) {
                val left = if (j > start) j - start + 1 else 1
                val right = if (end < nums.lastIndex) nums.lastIndex - end + 1 else 1
                count += left * right
                if (i + k - 1 < occurrences.size) break
            }
        }

        return count
    }
}
