class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $k
     * @return Integer
     */
    function countSubarrays($nums, $k) {
        $maxNum = max($nums);
        $count = 0;
        $currCount = 0;
        $result = 0;

        foreach ($nums as $num) {
            if ($num == $maxNum) {
                $currCount++;
                if ($currCount >= $k) {
                    $count++;
                }
            } else {
                if ($currCount >= $k) {
                    $result += $count;
                    $count = $currCount;
                } else {
                    $count = 0;
                }
                $currCount = 0;
            }
        }

        if ($currCount >= $k) {
            $result += $count;
        }

        return $result;
    }
}
