class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $k
     * @return Integer
     */
    function countSubarrays($nums, $k) {
        $totalSubarrays = 0;
        $maxNum = max($nums);
        $freq = []; // To keep track of frequencies of maxNum
        $n = count($nums);

        for ($i = 0; $i < $n; $i++) {
            if ($nums[$i] == $maxNum) {
                $freq[] = $i;
            }
        }

        for ($i = 0; $i < count($freq); $i++) {
            // Left boundary
            $left = $i > 0 ? $freq[$i - 1] : -1;
            // Right boundary
            $right = $i + $k - 1 < count($freq) ? $freq[$i + $k - 1] : $n;

            if ($right == $n) break; // If we don't have enough to make k appearances

            // Next index beyond the right boundary where maxNum appears
            $nextRight = $i + $k < count($freq) ? $freq[$i + $k] : $n;

            // Calculate subarrays
            $totalSubarrays += ($freq[$i] - $left) * ($nextRight - $right);
        }

        return $totalSubarrays;
    }
}
