from typing import List

class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        max_num = max(nums)
        indexes = [i for i, v in enumerate(nums) if v == max_num]
        ans = 0
        nums.append(0)  # Sentinel to handle subarray ending at the last element
        for start in range(len(nums) - 1):
            count = 0
            for end in range(start, len(nums)):
                if nums[end] == max_num:
                    count += 1
                if count == k:
                    # Find the next index of max_num or the end of the array
                    next_index = next((i for i in indexes if i > end), len(nums) - 1)
                    ans += next_index - end
                    break
        return ans
