object Solution {
    def countSubarrays(nums: Array[Int], k: Int): Long = {
        val maxVal = nums.max
        val indices = nums.indices.filter(nums(_) == maxVal).toArray
        var count = 0L

        if (indices.length < k) return 0

        for (i <- indices.indices) {
            val left = if (i == 0) indices(i) else indices(i) - indices(i - 1)
            val right = if (i == indices.length - 1) nums.length - indices(i) - 1 else indices(i + 1) - indices(i) - 1
            if (i < k - 1) count += (left + 1) * (right + 1)
            if (i >= k - 1) {
                val prevLeft = if (i == k - 1) indices(i - k + 1) + 1 else indices(i - k + 1) - indices(i - k)
                count += prevLeft * (right + 1)
            }
        }
        count
    }
}
