object Solution {
    def countSubarrays(nums: Array[Int], k: Int): Long = {
        val maxVal = nums.max
        var total = 0L
        var start = 0
        var end = 0
        var count = 0

        while (start < nums.length) {
            end = start
            count = 0
            while (end < nums.length && (nums(end) == maxVal || count < k)) {
                if (nums(end) == maxVal) count += 1
                if (count >= k) total += 1
                end += 1
            }
            total += (end - start - 1) * (end - start) / 2
            start = end + 1
        }

        total
    }
}
