class Solution {
public:
    int minimizeConcatenatedLength(vector<string>& words) {
        vector<vector<int>> overlap(words.size(), vector<int>(words.size()));
        for (int i = 0; i < words.size(); ++i) {
            for (int j = 0; j < words.size(); ++j) {
                if (i != j) {
                    for (int k = min(words[i].size(), words[j].size()); k > 0; --k) {
                        if (words[i].substr(words[i].size() - k) == words[j].substr(0, k)) {
                            overlap[i][j] = k;
                            break;
                        }
                    }
                }
            }
        }

        int n = words.size();
        vector<vector<int>> dp(1 << n, vector<int>(n, INT_MAX / 2));
        for (int i = 0; i < n; ++i) {
            dp[1 << i][i] = words[i].length();
        }

        for (int mask = 1; mask < (1 << n); ++mask) {
            for (int j = 0; j < n; ++j) {
                if (mask & (1 << j)) {
                    int pmask = mask ^ (1 << j);
                    for (int k = 0; k < n; ++k) {
                        if (pmask & (1 << k)) {
                            dp[mask][j] = min(dp[mask][j], dp[pmask][k] + (int)words[j].length() - overlap[k][j]);
                        }
                    }
                }
            }
        }

        int result = INT_MAX / 2;
        for (int i = 0; i < n; ++i) {
            result = min(result, dp[(1 << n) - 1][i]);
        }
        return result;
    }
};
