class Solution {
  int minimizeConcatenatedLength(List<String> words) {
    int n = words.length;
    List<int> dp = List.filled(1 << n, 10000);
    List<int> concatLength = List.filled(1 << n, 0);
    dp[0] = 0;

    for (int mask = 0; mask < (1 << n); mask++) {
      for (int i = 0; i < n; i++) {
        if ((mask & (1 << i)) == 0) {
          int nextMask = mask | (1 << i);
          int addition = words[i].length;
          for (int j = 0; j < n; j++) {
            if ((mask & (1 << j)) != 0) {
              if (words[i][0] == words[j][words[j].length - 1] || words[j][0] == words[i][words[i].length - 1]) {
                addition = words[i].length - 1;
              }
            }
          }
          if (dp[nextMask] > dp[mask] + addition) {
            dp[nextMask] = dp[mask] + addition;
            concatLength[nextMask] = concatLength[mask] + words[i].length;
          }
        }
      }
    }

    return dp[(1 << n) - 1];
  }
}
