func minimizeConcatenatedLength(words []string) int {
    if len(words) == 1 {
        return len(words[0])
    }

    join := func(x, y string) int {
        if x[len(x)-1] == y[0] {
            return len(x) + len(y) - 1
        }
        return len(x) + len(y)
    }

    dp := make([][]int, len(words))
    for i := range dp {
        dp[i] = make([]int, len(words))
    }

    for l := 2; l <= len(words); l++ {
        for i := 0; i <= len(words)-l; i++ {
            j := i + l - 1
            dp[i][j] = 10000 // A large number
            for k := i; k < j; k++ {
                dp[i][j] = min(dp[i][j], dp[i][k]+dp[k+1][j]-1)
                dp[i][j] = min(dp[i][j], join(words[i], words[j])+k-i+j-k-1)
            }
            if l == 2 {
                dp[i][j] = join(words[i], words[j])
            }
        }
    }

    return dp[0][len(words)-1]
}

func min(a, b int) int {
    if a < b {
        return a
    }
    return b
}
