class Solution {
    public int minimizeConcatenatedLength(String[] words) {
        int n = words.length;
        if (n == 1) return words[0].length();

        int[][] overlap = new int[n][n];

        // Calculate maximum overlap between each pair of words
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (i != j) {
                    for (int k = Math.min(words[i].length(), words[j].length()); k > 0; k--) {
                        if (words[i].endsWith(words[j].substring(0, k))) {
                            overlap[i][j] = k;
                            break;
                        }
                    }
                }
            }
        }

        // dp[mask][i] stores the minimum length of concatenation ending with words[i]
        int[][] dp = new int[1 << n][n];
        for (int i = 0; i < (1 << n); i++) {
            Arrays.fill(dp[i], Integer.MAX_VALUE);
        }

        for (int i = 0; i < n; i++) {
            dp[1 << i][i] = words[i].length();
        }

        for (int mask = 1; mask < (1 << n); mask++) {
            for (int last = 0; last < n; last++) {
                if ((mask & (1 << last)) != 0) {
                    for (int i = 0; i < n; i++) {
                        if ((mask & (1 << i)) == 0) {
                            int nextMask = mask | (1 << i);
                            dp[nextMask][i] = Math.min(dp[nextMask][i], dp[mask][last] + words[i].length() - overlap[last][i]);
                        }
                    }
                }
            }
        }

        int minLen = Integer.MAX_VALUE;
        for (int i = 0; i < n; i++) {
            minLen = Math.min(minLen, dp[(1 << n) - 1][i]);
        }

        return minLen;
    }
}
