class Solution {
    public int minimizeConcatenatedLength(String[] words) {
        // Initialize the dp array with -1, denoting uncomputed states
        int n = words.length;
        int[][] dp = new int[1 << n][n];
        for (int[] row : dp) {
            Arrays.fill(row, -1);
        }

        // Precompute the savings for each pair of words when one follows the other
        int[][] savings = new int[n][n];
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < n; ++j) {
                if (i != j) {
                    int minLen = Math.min(words[i].length(), words[j].length());
                    for (int k = 1; k <= minLen; ++k) {
                        if (words[i].endsWith(words[j].substring(0, k))) {
                            savings[i][j] = k;
                        }
                    }
                }
            }
        }

        // Recursive function to compute the minimum length
        return minLength(words, savings, dp, (1 << n) - 1, 0) + words[0].length();
    }

    private int minLength(String[] words, int[][] savings, int[][] dp, int mask, int last) {
        if (mask == (1 << last)) return 0; // Base case: only one word is chosen
        if (dp[mask][last] != -1) return dp[mask][last]; // Return the computed result if already done

        int result = Integer.MAX_VALUE;
        for (int i = 0; i < words.length; ++i) {
            if ((mask & (1 << i)) != 0 && i != last) {
                // Attempt to place words[i] before the current 'last' word
                int prevMask = mask ^ (1 << last); // Remove 'last' word from the current combination
                int cost = minLength(words, savings, dp, prevMask, i) + words[last].length() - savings[i][last];
                result = Math.min(result, cost);
            }
        }

        dp[mask][last] = result; // Memoize the result
        return result;
    }
}
